import * as React from 'react';
import styled from 'styled-components';

type LayoutProps = {
    gap : number
}

const Row = styled.div<LayoutProps>`
    display: flex;
    flex-direction: Row;
    gap: ${(props) => props.gap}px;
`;

const Column = styled.div<LayoutProps>`
    display: flex;
    flex-direction: Column;
    gap: ${(props) => props.gap}px;
`;

const Board = () => {
    return (
        <div>TODO: Board</div>
    )
}

const Log = () => {
    return(
        <ol>
            <li><button>Go to move</button></li>
        </ol>
    )
}

const StyledSquare = styled.button`
    width: 34px;
    height: 34px;
    background: #fff;
    border: 1px dolid #999;
    padding: 0;
    font-size: 24px;
    font-weight: bold;
`;

const Square = () => {
    return(
        <StyledSquare>
            X
        </StyledSquare>
    )
}


const Game = () => {
    return(
        <Column gap={0}>
            <Row gap={0}>
                <Square/>
                <Square/>
                <Square/>
            </Row>
        </Column>
    )
}

export default Game
