import * as React from 'react';
import styled from 'styled-components';
import { useGameState, BoardState } from './GameStatus';

type LayoutProps = {
    gap : number
}

const Row = styled.div<LayoutProps>`
    display: flex;
    flex-direction: Row;
    gap: ${(props) => props.gap}px;
`;

const Column = styled.div<LayoutProps>`
    display: flex;
    flex-direction: Column;
    gap: ${(props) => props.gap}px;
`;

type BoardProps = {
    board: BoardState,
    onClick: (square: number) => void
}

const Board = ({board, onClick} : BoardProps) => {
    return (
        <Column gap={0}>
        <Row gap={0}>
            <Square/>
            <Square/>
            <Square/>
        </Row>
        <Row gap={0}>
            <Square/>
            <Square/>
            <Square/>
        </Row>
        <Row gap={0}>
            <Square/>
            <Square/>
            <Square/>
        </Row>
    </Column>
    )
}

const Log = () => {
    return(
        <ol>
            <li><button>Go to move</button></li>
        </ol>
    )
}

const StyledSquare = styled.button`
    width: 34px;
    height: 34px;
    background: #fff;
    border: 1px dolid #999;
    padding: 0;
    font-size: 24px;
    font-weight: bold;
`;



const Square = () => {
    return(
        <StyledSquare>
            X
        </StyledSquare>
    )
}

const Game = () => {

    const {
        gameState, 
        current,
        xIsNext,
        jumpTo,
        winner,
        handleClick,
    } = useGameState();

    return(
        <Row gap={20}>
            <Column gap={20}>
                <div>
                    { winner ? `Winner ${winner}` : `Next Player ${xIsNext ? 'X' : 'O'}` }
                </div>
                <Board board={current} onClick={handleClick} />
            </Column>
            <Log/>
        </Row>
    )
}

export default Game
